import React, { useEffect, useState } from "react";
import axios from "axios";

function Todo() {
    const [todoList, setTodoList] = useState([]);
    const [newTask, setNewTask] = useState("");
    const [newStatus, setNewStatus] = useState("");
    const [newDeadline, setNewDeadline] = useState("");

    // Fetch tasks from backend
    const fetchTasks = () => {
        axios
            .get("http://127.0.0.1:3001/getTodoList")
            .then((result) => setTodoList(result.data))
            .catch((err) => {
                console.error("Error fetching tasks:", err);
                alert("Failed to fetch tasks. Please try again.");
            });
    };

    useEffect(() => {
        fetchTasks();
    }, []);

    // Add new task to the list
    const addTask = (e) => {
        e.preventDefault();
        const taskName = newTask.trim();

        if (!taskName || !newStatus || !newDeadline) {
            alert("All fields must be filled out.");
            return;
        }

        axios
            .post("http://127.0.0.1:3001/addTodoList", {
                task: taskName,
                status: newStatus,
                deadline: newDeadline,
            })
            .then(() => {
                setNewTask(""); // Clear the input fields
                setNewStatus("");
                setNewDeadline("");
                fetchTasks(); // Re-fetch the updated task list
            })
            .catch((err) => {
                console.error("Error adding task:", err);
                alert("Failed to add task. Please try again.");
            });
    };

    // Delete a task
    const deleteTask = (id) => {
        if (window.confirm("Are you sure you want to delete this task?")) {
            axios
                .delete(`http://127.0.0.1:3001/deleteTodoList/${id}`)
                .then(() => fetchTasks())
                .catch((err) => {
                    console.error("Error deleting task:", err);
                    alert("Failed to delete task. Please try again.");
                });
        }
    };

    return (
        <div className="flex items-center justify-center min-h-screen bg-gray-100">
            <div className="w-full max-w-4xl bg-white shadow-lg rounded-lg p-6">
                <h1 className="text-3xl font-bold text-center mb-6 text-blue-600">Todo List</h1>

                {/* Add New Task Form */}
                <form onSubmit={addTask} className="mb-6">
                    <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                        <input
                            type="text"
                            placeholder="Enter the task name"
                            value={newTask}
                            onChange={(e) => setNewTask(e.target.value)}
                            className="p-3 border rounded"
                        />
                        <select
                            value={newStatus}
                            onChange={(e) => setNewStatus(e.target.value)}
                            className="p-3 border rounded"
                        >
                            <option value="">Select Status</option>
                            <option value="Pending">Pending</option>
                            <option value="In Progress">In Progress</option>
                            <option value="Completed">Completed</option>
                        </select>
                        <input
                            type="datetime-local"
                            value={newDeadline}
                            onChange={(e) => setNewDeadline(e.target.value)}
                            className="p-3 border rounded"
                            placeholder="Enter deadline"
                        />
                    </div>
                    <button
                        type="submit"
                        className="mt-4 w-full bg-blue-500 text-white py-2 rounded hover:bg-blue-600"
                    >
                        Add Task
                    </button>
                </form>

                {/* Task List */}
                <table className="w-full border-collapse">
                    <thead>
                        <tr className="bg-gray-200 text-gray-600">
                            <th className="border p-3 text-left">Task</th>
                            <th className="border p-3 text-left">Status</th>
                            <th className="border p-3 text-left">Deadline</th>
                            <th className="border p-3 text-center">Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {todoList.map((todo) => (
                            <tr key={todo._id} className="hover:bg-gray-50">
                                <td className="border p-3">{todo.task}</td>
                                <td className="border p-3">{todo.status}</td>
                                <td className="border p-3">
                                    {todo.deadline
                                        ? new Date(todo.deadline).toLocaleString()
                                        : "No deadline"}
                                </td>
                                <td className="border p-3 text-center">
                                    <div className="flex justify-center space-x-2">
                                        <button
                                            onClick={() => deleteTask(todo._id)}
                                            className="bg-red-500 text-white px-3 py-1 rounded hover:bg-red-600"
                                        >
                                            Delete
                                        </button>
                                    </div>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </div>
    );
}

export default Todo;
